    },
    {
      proposalId: failedFunding.id,
      voterId: eve.id,
      support: false,
      wanAmount: "10000",
      lockType: "12_months",
      votingPower: "100000",
      processed: true,
    },
  ]);

  const [passedParameter] = await db.insert(proposals).values({
    title: "Increase Voting Period to 21 Days",
    description: "Extend the voting period from 14 to 21 days to give community members more time to review complex proposals and participate in governance decisions.",
    type: "parameter",
    status: "passed",
    creatorId: eve.id,
    stakeAmount: "800",
    stakeType: "lock",
    xpBurned: "110000",
    publicizedAt: fourteenDaysAgo,
    votingStartsAt: sevenDaysAgo,
    votingEndsAt: threeDaysAgo,
    votesFor: "200000",
    votesAgainst: "50000",
  }).returning();
  console.log("  ✓ Created PASSED parameter proposal");

  await db.insert(votes).values([
    {
      proposalId: passedParameter.id,
      voterId: alice.id,
      support: true,
      wanAmount: "10000",
      lockType: "12_months",
      votingPower: "100000",
      processed: true,
    },
    {
      proposalId: passedParameter.id,
      voterId: charlie.id,
      support: true,
      wanAmount: "8000",
      lockType: "12_months",
      votingPower: "80000",
      processed: true,
    },
    {
      proposalId: passedParameter.id,
      voterId: bob.id,
      support: false,
      wanAmount: "5000",
