    status: "failed",
    creatorId: charlie.id,
    stakeAmount: "300",
    stakeType: "lock",
    xpBurned: "110000",
    publicizedAt: fourteenDaysAgo,
    votingStartsAt: sevenDaysAgo,
    votingEndsAt: threeDaysAgo,
    votesFor: "60000",
